__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sauna fast-on weekday start",
    "parent": 0,
    "comment": "Start sauna to 70% with 1.0 minute ramp when user fast-ons the sauna on weekdays between 06:00-22:00",
    "if": [
      { "==": { "device": "1D CE 7E 1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "22:00" } } } }
    ],
    "then": [
      { "device": "1D CE 7E 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "1D CE 7E 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) so a physical fast-on triggers the routine. Restrict to weekdays 06:00-22:00. Set ramp rate enum index 12 corresponding to 1.0 minute, then set On to 70% using DON parameter. No else actions required."
  }
}
__END_NUCORE_ROUTINE__
