__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Poll stephbath on leak or daily 08:00",
    "parent": 0,
    "comment": "When stephbath reports On (COS) or every day at 08:00, query stephbath and hwtank WET",
    "if": [
      { "==": { "device": "2D 62 1E 4", "status": "ST", "value": 100, "uom": "51" } },
      { "or": 1 },
      { "at": { "time": "08:00" } }
    ],
    "then": [
      { "device": "2D 62 1E 4", "command": "QUERY" },
      { "device": "2D 5E 6C 2", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "Use a COS trigger when the stephbath sensor changes to On (value 100, uom 51) or a daily schedule at 08:00. The action is to query both the stephbath sensor and the hwtank WET sensor to confirm states — querying uses the devices' accepted QUERY command."
  }
}
__END_NUCORE_ROUTINE__
