__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Sump main On alert and queries",
    "parent": 0,
    "comment": "When sump main changes to On (COS), query and beep; on Off, query",
    "if": [
      { ">=": { "device": "55 8 AB 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "55 8 AB 4", "command": "QUERY" },
      { "device": "55 8 AB 4", "command": "BEEP", "parameters": [ { "name": "n/a", "value": 1, "uom": "100", "precision": 0 } ] }
    ],
    "else": [
      { "device": "55 8 AB 4", "command": "QUERY" }
    ],
    "reasoning": "Use a COS-style comparison to detect transition to On (value 100). Then query to refresh state and beep to alert. Else (when not >=100) query to confirm Off state. Commands used are from the device's accepts list (QUERY and BEEP)."
  }
}
__END_NUCORE_ROUTINE__
