__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Living Room DFON weekday evening backlight bright",
    "parent": 0,
    "comment": "When Living Room receives DFON (fast on) during weekday evenings set its backlight to max index",
    "if": [
      { "(": 1 },
        { "==": { "device": "F 95 DA 6", "control": "DFON" } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "F 95 DA 6", "command": "BL", "parameters": [ { "name": "n/a", "value": 127, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) from the Living Room combined with a weekday evening schedule to only change the Living Room backlight via the BL accept command to the maximum index (127) for brightest indicator."
  }
}
__END_NUCORE_ROUTINE__
