__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Turn on relay 002 when rear deck sensor opens overnight",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "ZY089_104", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY056_002_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the rear deck sensor reports a COS to On during overnight hours, enable the relay to provide illumination or power only at night. Uses exact sensor ST value 100 and the overnight schedule to limit activation."
  }
}
__END_NUCORE_ROUTINE__
