__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Front Porch on if Turret DFON or Eaves turned On",
    "parent": 0,
    "comment": "",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "(": 1 },
        { "==": { "device": "21 B 80 1", "control": "DFON" } },
        { "or": 1 },
        { ">=": { "device": "21 D 9D 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { ")": 1 }
    ],
    "then": [
      { "device": "21 F 97 1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "At sunset, ensure Front Porch is turned on when either the Turret was manually fast-on (COC DFON) or the Eaves relay has just changed to On (COS ST >= On). This ensures lighting when either manual action or Eaves state indicates occupancy/need."
  }
}
__END_NUCORE_ROUTINE__
