__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday Morning A-button: Turn on garage + setup",
    "parent": 0,
    "comment": "When A is physically used (DFON) between 06:00 and 08:00 on weekdays, power on main, set RR to 1.0 minute, and set B backlight index 31.",
    "if": [
      { "(": 1 },
        { "==": { "device": "3D 87 30 3", "control": "DFON" } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "08:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "3D 87 30 1", "command": "DON" },
      { "device": "3D 87 30 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "3D 87 30 4", "command": "BL", "parameters": [ { "name": "n/a", "value": 31, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "User requested an explicit physical button press on device A during weekday mornings; use a COC (DFON) on device 3D 87 30 3 combined with a weekly schedule for mon–fri 06:00–08:00. Actions use the main device's DON and RR commands (RR value 12 corresponds to 1.0 minute per device enum) and set the backlight on device B to index 31."
  }
}
__END_NUCORE_ROUTINE__
