__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night fast-on query garage",
    "parent": 0,
    "comment": "When DFON occurs overnight, poll both garage devices",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "2B 5 C0 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "2B 5 C0 1", "command": "QUERY" },
      { "device": "2B 5 C0 3", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "A COC (DFON) on the main garage during overnight hours likely indicates human activity; querying both garage devices immediately verifies their realtime status without changing device state."
  }
}
__END_NUCORE_ROUTINE__
