__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Notify and refresh on mailbox heartbeat",
    "parent": 0,
    "comment": "When Mailbox-Heartbeat reports On, send a local beep and query window sensors",
    "if": [
      { "==": { "device": "4E 3A 2A 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "4E 3A 2A 4", "command": "BEEP" },
      { "device": "37 3 5E 1", "command": "QUERY" },
      { "device": "37 3 5E 2", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "This routine listens for a COS on the Mailbox-Heartbeat device when its ST property becomes On (value 100). On that COS it uses accepted commands: BEEP on the mailbox to notify locally, and QUERY on both Window devices to refresh their reported states. Actions use only commands listed in each device's Accept Commands section."
  }
}
__END_NUCORE_ROUTINE__
