__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Door open after sunset -> Water Heater Light on until 23:00",
    "parent": 0,
    "comment": "Turn Water Heater Light on when Sliding Glass Door-Heartbeat opens after sunset until 23:00; otherwise turn it off.",
    "if": [
      { "from": { "sunset": 0, "to": { "time": "23:00", "day": 0 } } },
      { "and": 1 },
      { ">=": { "device": "4E 3A 92 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "C 0 A6 1", "command": "DON" }
    ],
    "else": [
      { "device": "C 0 A6 1", "command": "DOF" }
    ],
    "reasoning": "When the sliding door status becomes On after sunset (until 23:00), enable the Water Heater Light; otherwise ensure it is Off. Uses device ST enum value 100 for On and schedule window from sunset to 23:00."
  }
}
__END_NUCORE_ROUTINE__
