__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday daytime cooling - upstairs setpoint and fan",
    "parent": 0,
    "comment": "If upstairs temp >= 78°F between 09:00-17:00 Mon-Fri set heat setpoint to 72°F and fan On (hold next); otherwise set fan Auto.",
    "if": [
      { "(": 1 },
        { ">=": { "device": "n002_t521782496039", "status": "ST", "value": 780, "uom": "17", "precision": 1 } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "n002_t521782496039", "command": "CLISPH", "parameters": [ { "name": "n/a", "value": 720, "uom": "17", "precision": 1 } ] },
      { "device": "n002_t521782496039", "command": "CLIFS", "parameters": [ { "name": "n/a", "value": 1, "uom": "68", "precision": 0 }, { "name": "HoldType", "value": 1, "uom": "25", "precision": 0 } ] }
    ],
    "else": [
      { "device": "n002_t521782496039", "command": "CLIFS", "parameters": [ { "name": "n/a", "value": 0, "uom": "68", "precision": 0 } ] }
    ],
    "reasoning": "During warm weekdays we proactively lower upstairs heat setpoint to 72°F and enable fan On to increase cooling; when condition not met revert fan to Auto. Temperatures use uom 17 and precision 1 (value scaled x10)."
  }
}
__END_NUCORE_ROUTINE__
