__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "After-sunset: Master Bath turned On -> Close Closet & Query Fireplace",
    "parent": 0,
    "comment": "When Master Bath changes to On at sunset, ensure closet is off and query fireplace status",
    "if": [
      { "==": { "device": "ZY008_1", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "ZY009_1", "command": "DOF" },
      { "device": "ZY024_251", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "User requested action when Master Bath transitions to On after sunset. Use a COS check for Master Bath ST == On combined with a sunset schedule; then send DOF to Master Closet and QUERY to the Fireplace controller (fireplace only accepts QUERY)."
  }
}
__END_NUCORE_ROUTINE__
