__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening Fast-On Up adjusts Down brightness",
    "parent": 0,
    "comment": "When Up receives DFON between 18:00-22:00, check Down brightness and set or fade",
    "if": [
      { "==": { "device": "n001_88010de247650b", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "22:00", "day": 0 } } },
      { "and": 1 },
      { "<": { "device": "n001_88010de247650c", "status": "ST", "value": 50, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "n001_88010de247650c", "command": "SET_BRI", "parameters": [ { "name": "n/a", "value": 180, "uom": "100", "precision": 1 } ] }
    ],
    "else": [
      { "device": "n001_88010de247650c", "command": "FDUP", "parameters": [] }
    ],
    "reasoning": "COC trigger is DFON on Up; limit to evening with schedule; check Down ST (brightness percentage) and either set explicit brightness via SET_BRI or issue FDUP to increase smoothly."
  }
}
__END_NUCORE_ROUTINE__
