__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday Fast-On TV -> Warm Whole Wall",
    "parent": 0,
    "comment": "When TV fast-on during weekday evenings set Whole Wall to warm white",
    "if": [
      { "==": { "device": "n001_huegrp32", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "n001_huegrp33", "command": "SET_CTBR", "parameters": [ { "name": "Color Temperature", "value": 2700, "uom": "26", "precision": 1 }, { "name": "Transition Time", "value": 5000, "uom": "42", "precision": 1 }, { "name": "Brightness", "value": 150, "uom": "100", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) from the Living Room TV limited to weekday evenings to avoid accidental triggers. Apply SET_CTBR on the Whole Wall with a 5s transition for a smooth warm scene."
  }
}
__END_NUCORE_ROUTINE__
