__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Weekday IR Button R press monitor",
    "parent": 0,
    "comment": "Trigger when IR Button R is pressed during weekdays 09:00-17:00",
    "if": [
      { "weekly": { "days":"mon,tue,wed,thu,fri", "from": { "time":"09:00", "to": { "time":"17:00" } } } },
      { "and": 1 },
      { "==": { "device":"n009_irbutton_115", "control":"GV1" } }
    ],
    "then": [],
    "else": [],
    "reasoning": "This routine uses a COC-style equality on the device's Pressed send command (GV1) combined with a weekly schedule window. No controllable (accepts) devices were provided in the DEVICE STRUCTURE, so no actionable device commands are placed in \"then\". Provide target devices/actions to populate the then block."
  }
}
__END_NUCORE_ROUTINE__
