__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Activate Hub Garage when Garage starts after 18:00",
    "parent": 0,
    "comment": "When Garage sends Start during evening hours, set hub activity to Garage and stop music.",
    "if": [
      { "==": { "device": "n010_a37718611", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:59", "day": 0 } } }
    ],
    "then": [
      { "device": "n010_h51415fc31d16b", "command": "SET_ACTIVITY", "parameters": [ { "name": "n/a", "value": 1, "uom": "25", "precision": 0 } ] },
      { "device": "n010_a37718613", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on Garage Start (COC) limited to evening hours (from 18:00 to 23:59). Then set the Elite Hub activity enum to 'Garage' (value 1, uom 25) and ensure Music - Family Room is stopped (DOF)."
  }
}
__END_NUCORE_ROUTINE__
