__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Beep when kitchen leak sensor becomes On",
    "parent": 0,
    "comment": "Alert with device beep when bm leak kitch sink - Heartbea reports On",
    "if": [
      { "==": { "device": "55 A F6 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "55 A F6 4", "command": "BEEP", "parameters": [ { "name": "n/a", "value": 1, "uom": "100", "precision": 0 } ] },
      { "device": "55 A F6 4", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COS trigger on the leak sensor's ST property equaling On (100 UOM 51). Then call the device's BEEP command to provide an immediate audible alert and issue WDU to persist/log the change. No external notifications are used as requested."
  }
}
__END_NUCORE_ROUTINE__
