__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening Fast-On sets paired levels",
    "parent": 0,
    "comment": "Fast On pressed near sunset -> higher levels; otherwise lower levels",
    "if": [
      { "(": 1 },
        { "==": { "device": "1D C0 A7 1", "control": "DFON" } },
        { "and": 1 },
        { "at": { "sunset": -300 } },
      { ")": 1 }
    ],
    "then": [
      { "device": "1D C0 A7 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "1D C0 A7 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] },
      { "device": "1D C3 43 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "1D C0 A7 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] },
      { "device": "1D C3 43 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] }
    ],
    "reasoning": "User requests a different behavior when pressing Fast On near sunset: set a faster ramp (1.0 minute = enum value 12) and higher on-level (60%) for the initiating device and turn on the paired device to 60%. If Fast On occurs at other times, apply a lower 30% level to both."
  }
}
__END_NUCORE_ROUTINE__
