__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday leak or heartbeat alert: set garage backlight and beep wet sensor",
    "parent": 0,
    "comment": "On leak detected or manual heartbeat fast-on during work hours, make visual and audible alert",
    "if": [
      { "(": 1 },
        { "==": { "device": "2D 56 CF 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "2D 56 CF 4", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "1B 98 AB 6", "command": "BL", "parameters": [ { "name": "n/a", "value": 26, "uom": "25", "precision": 0 } ] },
      { "device": "2D 56 CF 2", "command": "BEEP", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Weekend-limited visual+audible alert: trigger when Wet sensor reports On (COS) or Heartbeat is manually fast-on (COC). Use garage Backlight BL (index 26) for visible cue and beep the wet sensor to draw attention. Weekly limiter prevents nighttime alerts."
  }
}
__END_NUCORE_ROUTINE__
