__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Craft Room Scene -> All On",
    "parent": 0,
    "comment": "When the craft room scene responder is activated, turn the craft room Switch All to All On.",
    "if": [
      { "==": { "device": "ZY012_259", "control": "QUERY" } }
    ],
    "then": [
      { "device": "ZY012_183", "command": "ALLON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC-style trigger from the scene responder (QUERY control) to send the ALLON command to the craft room Switch All device (ZY012_183). This directly maps the physical scene press to the All On action."
  }
}
__END_NUCORE_ROUTINE__
