{
  "routine": {
    "id": 1,
    "name": "Nightly off relay #1 at 22:00 if on",
    "parent": 0,
    "comment": "Turn off relay #1 nightly if it's on",
    "if": [
      { "at": { "time": "22:00" } },
      { "and": 1 },
      { ">=": { "device": "ZY056_001_1", "status": "ST", "value": 100, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY056_001_1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Schedule triggers at 22:00 daily. Check relay #1 status (ST >= On). If it's on, send DOF to ensure nightly shutdown and conserve power."
  }
}
__END_NUCORE_ROUTINE__
