__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query or Rediscover holidays on motion COS",
    "parent": 0,
    "comment": "When motion sensor ST changes to On -> Query holidays controller; when Off -> Re-Discover holidays controller",
    "if": [
      { "==": { "device": "ZY188_380", "status": "ST", "value": 100, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "n004_controller", "command": "QUERY", "parameters": [] }
    ],
    "else": [
      { "device": "n004_controller", "command": "DISCOVER", "parameters": [] }
    ],
    "reasoning": "Use a COS on ZY188_380 ST to detect motion (On=100). If COS to On, query the Holidays controller to refresh status; otherwise treat as Off and run Re-Discover to attempt reconnect. Uses only commands accepted by the Holidays controller (QUERY, DISCOVER) and the ST enum UOMs defined in DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
