__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on Toll Lamps when Under Cabs switch after 22:00",
    "parent": 0,
    "comment": "Activate LR2 to a defined level when KDR1 is physically switched on at night",
    "if": [
      { "==": { "device": "21 9B F5 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "26 AA 9E 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) on KDR1 together with a night-time window (22:00-06:00) to set LR2 to 70% with the On Level command. This matches manual control intent only during night hours."
  }
}
__END_NUCORE_ROUTINE__
