__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening Fast-On on Remote D triggers writes to C and F",
    "parent": 0,
    "comment": "When the user uses Fast On on Mini Remote - D after sunset, call Write Changes on the other remotes.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "3E EC F7 3", "control": "DFON" } }
    ],
    "then": [
      { "device": "3E EC F7 4", "command": "WDU" },
      { "device": "3E EC F7 5", "command": "WDU" }
    ],
    "else": [],
    "reasoning": "User action (DFON) on Mini Remote - D after sunset is the trigger. Only 'Write Changes' (WDU) is available as an accept command on devices, so the routine issues WDU to Mini Remote - C and Mini Remote - F to apply their configured changes. No parameters are specified because DEVICE STRUCTURE provides no parameters for WDU."
  }
}
__END_NUCORE_ROUTINE__
