__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening remote double-press sets fan light and motor",
    "parent": 0,
    "comment": "Triggered by Jennifer Remote DFON between 19:00-23:00 on weekdays",
    "if": [
      { "(": 1 },
      { "==": { "device": "70 87 E7 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "19:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "14 F6 C9 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 2 } ] },
      { "device": "14 F6 C9 2", "command": "DON", "parameters": [ { "name": "n/a", "value": 75, "uom": "51", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "Use DFON from the Jennifer Remote as a COC trigger; restrict to weekday evenings via weekly schedule. Then set the light brightness to 50% (DON with uom 51) and set the motor to Medium (DON with enum value 75)."
  }
}
__END_NUCORE_ROUTINE__
