__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night: door3 open triggers beep door4 and WDU BU H2O",
    "parent": 0,
    "comment": "When Sliding Glass Door.3 changes to On during overnight hours",
    "if": [
      { "==": { "device": "32 AF 85 3", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "32 AF 85 4", "command": "BEEP", "parameters": [] },
      { "device": "53 16 4 1", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on COS: Sliding Glass Door.3 ST becomes On (100). Restrict to overnight window using from 22:00 to 06:00 (cross-midnight). Actions use commands accepted by the target devices: BEEP on Sliding Glass Door.4 and WDU on BU H2O Heat Leak-Dry."
  }
}
__END_NUCORE_ROUTINE__
