__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Powder Mirror DFON -> Foyer Night 50%",
    "parent": 0,
    "comment": "When Powder Bath Mirror fast-on is pressed during night (22:00-06:00) set foyer to 50%",
    "if": [
      { "==": { "device": "55 99 67 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "4F A2 4E 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Separate routine triggers on the physical fast-on control (DFON) of the Powder Bath Mirror and applies a night time schedule (22:00-06:00 day wrap). It sets Foyer Can Lights to 50% using its DON command with percent parameter."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Powder Mirror DFON -> Foyer Day 100%",
    "parent": 0,
    "comment": "When Powder Bath Mirror fast-on is pressed during day (06:00-22:00) set foyer to 100%",
    "if": [
      { "==": { "device": "55 99 67 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "06:00", "to": { "time": "22:00", "day": 0 } } }
    ],
    "then": [
      { "device": "4F A2 4E 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Companion routine for daytime (06:00-22:00). Uses same DFON COC trigger and sets the Foyer Can Lights to full (100%)."
  }
}
__END_NUCORE_ROUTINE__
