__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on Address Sign Planter at sunset if off",
    "parent": 0,
    "comment": "Illuminate sign at sunset when currently off",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "<": { "device": "n020_huegrp88", "status": "ST", "value": 1, "uom": "51", "precision": 0 } }
    ],
    "then": [
      {
        "device": "n020_huegrp88",
        "command": "SET_CTBR",
        "parameters": [
          { "name": "Color Temperature", "value": 3000, "uom": "26", "precision": 1 },
          { "name": "Transition Time", "value": 1000, "uom": "42", "precision": 1 },
          { "name": "Brightness", "value": 200, "uom": "100", "precision": 1 }
        ]
      }
    ],
    "else": [],
    "reasoning": "At sunset, check if the planter is effectively off (status <1%). If so, set a warm white (3000K) with a 1s transition and brightness 200 to illuminate the address sign. Uses SET_CTBR which covers color temperature, transition time and brightness in one command."
  }
}
__END_NUCORE_ROUTINE__
