__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday Study Fast-On -> Warm Dim; else Master Bedroom on",
    "parent": 0,
    "comment": "Trigger on Study DFON during weekday mornings; otherwise ensure Master Bedroom is on and set.",
    "if": [
      { "(": 1 },
        { "==": { "device": "n001_huegrp3", "control": "DFON" } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "07:00", "to": { "time": "09:00" } } } },
      { ")": 1 }
    ],
    "then": [
      {
        "device": "n001_huegrp3",
        "command": "SET_CTBR",
        "parameters": [
          { "name": "K", "value": 2700, "uom": "26", "precision": 1 },
          { "name": "D", "value": 5000, "uom": "42", "precision": 1 },
          { "name": "BR", "value": 200, "uom": "100", "precision": 1 }
        ]
      }
    ],
    "else": [
      {
        "device": "n001_huegrp2",
        "command": "DON",
        "parameters": []
      },
      {
        "device": "n001_huegrp2",
        "command": "SET_BRI",
        "parameters": [
          { "name": "n/a", "value": 150, "uom": "100", "precision": 1 }
        ]
      }
    ],
    "reasoning": "Use a COC (DFON) on the Study during weekday mornings to apply a warm color temperature and high brightness with a short transition. If the Study fast-on event doesn't occur in that window, enable the Master Bedroom and set a moderate brightness as a fallback."
  }
}
__END_NUCORE_ROUTINE__
