__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn off Master Closet when Master Bath turns on in evening",
    "parent": 0,
    "comment": "When Master Bath Disco changes to On during the evening window, ensure Master Closet is Off",
    "if": [
      { "from": { "time": "18:00", "to": { "time": "23:59", "day": 0 } } },
      { "and": 1 },
      { "==": { "device": "ZY008_1", "status": "ST", "value": 100, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY009_1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a scheduled evening window plus a COS trigger (Master Bath ST == On). When that COS occurs during the window, send the Off command to Master Closet. No parameters required for DOF."
  }
}
__END_NUCORE_ROUTINE__
