__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Sunset: if remote 1 - E activated then turn remote 1 - H off",
    "parent": 0,
    "comment": "",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "(": 1 },
        { "==": { "device": "70 7F 45 6", "control": "DFON" } },
        { "or": 1 },
        { ">=": { "device": "70 7F 45 6", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { ")": 1 }
    ],
    "then": [
      { "device": "70 7F 45 7", "command": "WDU", "parameters": [ { "name": "ST", "value": 0, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "At sunset, respond to either a DFON control (COC) from remote 1 - E or its status changing to On (COS >=100). Then issue a WDU to remote 1 - H to write Status=0 (Off) using uom 51. Uses WDU because it is the device's accepted command."
  }
}
__END_NUCORE_ROUTINE__
