__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Enable Front Yard and Tree when Ent-Wall is off",
    "parent": 0,
    "comment": "Turn on lights at sunset only if the wall controller is off",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "1B 77 D5 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "13 7 26 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] },
      { "device": "1B 7A B0 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use sunset as the trigger and verify the Ent-Wall is off (status 0) to avoid conflicting local control; then send DON to Front Yard and Tree with the enum value 100 for On."
  }
}
__END_NUCORE_ROUTINE__
