__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Laundry Light - On at Sunset or Fast On",
    "parent": 0,
    "comment": "Turn the exterior laundry door light on at sunset or when the switch is fast-on (DFON)",
    "if": [
      { "at": { "sunset": 0 } },
      { "or": 1 },
      { "==": { "device": "2A 60 99 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "2A 60 99 1", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Use a schedule trigger (sunset) OR a COC (DFON) to turn the light on. OR ensures either event will execute the same ON action."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Laundry Light - Off at 23:00",
    "parent": 0,
    "comment": "Ensure the exterior laundry door light is turned off nightly at 23:00",
    "if": [
      { "at": { "time": "23:00" } }
    ],
    "then": [
      { "device": "2A 60 99 1", "command": "DOF" }
    ],
    "else": [],
    "reasoning": "A daily time-triggered routine ensures the light turns off every night at the specified time."
  }
}
__END_NUCORE_ROUTINE__
