__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "IR Button press toggles LIFX in evening",
    "parent": 0,
    "comment": "When IR Button A is pressed between 18:00-23:00 toggle or set LIFX",
    "if": [
      { "==": { "device":"n009_irbutton_104","control":"GV1" } },
      { "and": 1 },
      { "from": { "time":"18:00","to":{ "time":"23:00","day":0 } } },
      { "and": 1 },
      { "==": { "device":"n002_d073d52ee564","status":"GV5","value":1,"uom":"2","precision":0 } },
      { "and": 1 },
      { ">": { "device":"n002_d073d52ee564","status":"GV3","value":10000,"uom":"56","precision":1 } }
    ],
    "then": [
      { "device":"n002_d073d52ee564","command":"DOF","parameters":[] }
    ],
    "else": [
      { "device":"n002_d073d52ee564","command":"SET_COLOR","parameters":[{ "name":"n/a","value":10,"uom":"25","precision":0 }] },
      { "device":"n002_d073d52ee564","command":"SETB","parameters":[{ "name":"n/a","value":40000,"uom":"56","precision":1 }] }
    ],
    "reasoning": "Pressing IR Button A between 18:00 and 23:00 triggers the routine. If LIFX is online and bright, turn it off. Otherwise set warm white and high brightness."
  }
}
__END_NUCORE_ROUTINE__
