__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Couch Right quick evening DFON -> set level & ramp",
    "parent": 0,
    "comment": "Trigger on physical Fast On during evening window",
    "if": [
      { "==": { "device": "35 1E 43 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "21:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      {
        "device": "35 1E 43 1",
        "command": "OL",
        "parameters": [
          { "name": "n/a", "value": 30, "uom": "51", "precision": 0 }
        ]
      },
      {
        "device": "35 1E 43 1",
        "command": "RR",
        "parameters": [
          { "name": "n/a", "value": 28, "uom": "25", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) combined with a time window to ensure this reaction only happens when the user physically fast-ons the Couch Right in the evening. Then set On Level to 30% and apply the Ramp Rate enum value 28 (0.5 seconds) which is defined in the device's Ramp Rate enumerations."
  }
}
__END_NUCORE_ROUTINE__
