__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Gazebo Two fast-on evening: set level and ramp",
    "parent": 0,
    "comment": "When Gazebo Two is physically fast-on between 18:00-23:00, set level to 80% and ramp rate to 5.0 minutes.",
    "if": [
      { "==": { "device": "51 C5 C3 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "51 C5 C3 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "51 C5 C3 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 4, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "COC trigger (DFON) ensures action only when user physically fast-on the device. Time window restricts to evening. Uses On Level (uom 51) value 80 and Ramp Rate enum value 4 which maps to 5.0 minutes (uom 25)."
  }
}
__END_NUCORE_ROUTINE__
