__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Leak detected -> beep + set HVAC Off",
    "parent": 0,
    "comment": "When kitchen leak sensor reports On, alert and turn HVAC mode off",
    "if": [
      { "==": { "device": "55 A F6 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "55 A F6 4", "command": "BEEP" },
      { "device": "25 18 A2 1", "command": "Mode", "parameters": [ { "name": "n/a", "value": 0, "uom": "98", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on COS: leak sensor ST equals On (uom 51 value 100). Then use the sensor's BEEP command to alert. Also set the main thermostat Mode to Off using the Mode command with enum value 0 (uom 98). All IDs and uoms come from the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
