__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday double-tap: SofaAreaLights to 60% with 1min ramp",
    "parent": 0,
    "comment": "Double-tap Ceiling.Lights toggles SofaAreaLights to 60% using 1.0 minute ramp during weekday evenings.",
    "if": [
      { "(": 1 },
        { "==": { "device": "70 37 C0 1", "control": "DFON", "parameters": [ { "name": "tapCount", "value": 2, "uom": "108", "precision": 0 } ] } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "70 42 BD 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "70 42 BD 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (double tap) from Ceiling.Lights (DFON with tapCount=2) restricted to weekdays 18:00-23:00. First set SofaAreaLights Ramp Rate to 1.0 minute (RR enum value 12) then send DON to 60% so the device ramps to target level." 
  }
}
__END_NUCORE_ROUTINE__
