__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Foyer on when living room off",
    "parent": 0,
    "comment": "At sunset turn foyer to 50% if living room is off; otherwise reduce living room to 30%",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "56 82 72 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "1A 57 EF 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "56 82 72 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] }
    ],
    "reasoning": "Use sunset schedule plus living room status check. If living room is off (ST==0) set foyer DON to 50% using its DON parameter. If living room is not off, reduce living room level to 30% via its OL command. This avoids guessing IDs or units and uses only provided commands and UOM 51."
  }
}
__END_NUCORE_ROUTINE__
