__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Turn on external fan when tamper alarm ON and fan responding",
    "parent": 0,
    "comment": "Handles tamper COS with fan responding check",
    "if": [
      { "==": { "device": "ZY189_157", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "==": { "device": "ZY104_001_1", "status": "ERR", "value": 0, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY104_001_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] }
    ],
    "else": [
      { "device": "ZY104_001_1", "command": "QUERY", "parameters": [] }
    ],
    "reasoning": "Trigger on tamper alarm change to On (COS). Only actuate the fan if it is responding (ERR == True). If not, query the fan to get its current state for diagnostics. Uses existing device commands and properties from DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
