__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset - landscape lights to 70% with 30s ramp",
    "parent": 0,
    "comment": "At sunset -5min ensure device responds, set 30s ramp then set level to 70%",
    "if": [
      { "at": { "sunset": -300 } },
      { "and": 1 },
      { "==": { "device": "43 36 57 1", "status": "ERR", "value": 0, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "43 36 57 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 18, "uom": "25", "precision": 0 } ] },
      { "device": "43 36 57 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Schedule at sunset minus 5 minutes ensures the lights begin ramping into evening; check Responding == True (ERR=0) to avoid acting on an unresponsive device. Set RR to enum value 18 (30.0 seconds) then issue DON with 70% (uom 51)."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Weeknight keypad fast-on -> TV Cans 30% with short ramp",
    "parent": 0,
    "comment": "Keypad DFON or DON during weekdays evenings sets TV Cans ramp then level",
    "if": [
      { "(": 1 },
        { "==": { "device": "1B EB AF 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "1B EB AF 1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "71 30 9 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 26, "uom": "25", "precision": 0 } ] },
      { "device": "71 30 9 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use COC (DFON or DON control) from the keypad during weekday evenings to trigger TV Cans. Set Ramp Rate to enum 26 (4.5 seconds) for a short smooth transition, then set level to 30% (uom 51). Grouped triggers allow either DFON or DON to act as the trigger."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 103,
    "name": "Night rule: TV Cans >50% -> turn off and enable landscape 20%",
    "parent": 0,
    "comment": "If TV Cans changes to >=50% during nighttime, shut cans off and light landscape",
    "if": [
      { "(": 1 },
        { ">=": { "device": "71 30 9 1", "status": "ST", "value": 50, "uom": "51", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "71 30 9 1", "command": "DOF", "parameters": [] },
      { "device": "43 36 57 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 20, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Detect COS when TV Cans status rises to >=50% (ST >= 50). Limited to the nightly 22:00-06:00 window (cross-midnight). Immediately turn the cans off (DOF) and set landscape lights to 20% to provide modest illumination."
  }
}
__END_NUCORE_ROUTINE__
