__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Set IR Linc backlights when Spare.Relay turns ON at sunset",
    "parent": 0,
    "comment": "When Spare.Relay.1 is ON at sunset, set IR Linc 1 and 2 backlights to 80%",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "29 3F 72 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "1B 74 15 0", "command": "BL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "22 7E 84 0", "command": "BL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "When Spare.Relay.1 reports ON at sunset (COS), update both IR Linc devices' backlight to 80% to provide visual indication. Uses the Spare.Relay status property and sunset schedule."
  }
}
__END_NUCORE_ROUTINE__
