__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Media Room On if Thermostat Not Frost Protection",
    "parent": 0,
    "comment": "Turn on ceiling dimmer at sunset unless thermostat setback is Frost Protection",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "=!": { "device": "ZY007_1", "status": "CLISBS", "value": 1, "uom": "25" } }
    ],
    "then": [
      { "device": "22 E8 5 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "At sunset, enable the Media Room ceiling dimmer to a comfortable 60% unless the thermostat indicates Frost Protection (CLISBS=1), in which case lights are not forced on to avoid conflicting safety/energy modes."
  }
}
__END_NUCORE_ROUTINE__
