__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Close garage if opened after sunset+10min",
    "parent": 0,
    "comment": "If the garage door sensor changes to On (open) at 10 minutes after sunset, pulse the relay to close the door.",
    "if": [
      { "==": { "device": "22 78 C8 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "at": { "sunset": 600 } }
    ],
    "then": [
      { "device": "22 78 C8 2", "command": "DFON", "parameters": [] },
      { "device": "22 78 C8 2", "command": "DFOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on the sensor's COS to On combined with the schedule 'at sunset + 600s'. When both are true, send a fast on then fast off to the relay to emulate a button press and close the door." 
  }
}
__END_NUCORE_ROUTINE__
