__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night garage-open alert",
    "parent": 0,
    "comment": "When garage opens at night, enable relay and verify sensor",
    "if": [
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"28 C8 CE 1","status":"ST","value":100,"uom":"51","precision":0 } }
    ],
    "then": [
      { "device":"28 C8 CE 2","command":"DFON" },
      { "device":"28 C8 CE 1","command":"QUERY" }
    ],
    "else": [],
    "reasoning": "Trigger on a COS of the Garage Double Door Sensor becoming On during overnight hours. Use DFON on the Relay to provide immediate power/lighting and QUERY the sensor to confirm state." 
  }
}
__END_NUCORE_ROUTINE__
