__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sensor beep and query at sunset when motion detected",
    "parent": 0,
    "comment": "Beep and query sensor when it turns ON at sunset",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "31 AB F6 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "31 AB F6 1", "command": "BEEP" },
      { "device": "31 AB F6 1", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "At sunset when the sensor reports ON (COS), trigger a BEEP to alert and then QUERY to refresh status. Both BEEP and QUERY exist in the device's accepts list and require no explicit parameter values in the structure provided."
  }
}
__END_NUCORE_ROUTINE__
