__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning: High tap sets High on, others off",
    "parent": 0,
    "comment": "Triggered by physical DFON on Gym KP.C during weekday mornings",
    "if": [
      { "==": { "device": "51 71 F6 3", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "51 71 F6 3", "command": "WDU", "parameters": [ { "name": "ST", "value": 100, "uom": "51", "precision": 0 } ] },
      { "device": "51 71 F6 4", "command": "WDU", "parameters": [ { "name": "ST", "value": 0, "uom": "51", "precision": 0 } ] },
      { "device": "51 71 F6 5", "command": "WDU", "parameters": [ { "name": "ST", "value": 0, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "A COC (DFON) on Gym KP.C during weekday 06:00-09:00 indicates an explicit user request for High speed; write ST=100 to KP.C and ST=0 to KP.D and KP.E to ensure only the chosen speed runs. Uses WDU to write the Status property (uom 51)."
  }
}
__END_NUCORE_ROUTINE__
