__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekend manual laser triggers group on",
    "parent": 0,
    "comment": "",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY011_001_1", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY011_002_1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "sat,sun", "from": { "time": "20:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "ZY011_1", "command": "DON" }
    ],
    "else": [
      { "device": "ZY011_1", "command": "DOF" }
    ],
    "reasoning": "Turn group on when either left or right laser is manually turned on during weekend evenings; otherwise turn group off."
  }
}
__END_NUCORE_ROUTINE__
