__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Set D On when C Fast On and C responding",
    "parent": 0,
    "comment": "Activate remote 1 - D when remote 1 - C sends a Fast On and is responding",
    "if": [
      { "==": { "device": "70 7F 45 4", "control": "DFON" } },
      { "and": 1 },
      { "==": { "device": "70 7F 45 4", "status": "ERR", "value": 0, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "70 7F 45 3", "command": "WDU", "parameters": [ { "name": "ST", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on C sending DFON (COC). Only act when C is responding (ERR == 0 True). Use WDU on device D to set its Status (ST) to On (100%)."
  }
}
__END_NUCORE_ROUTINE__
