__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night entry light and mobile SMS on front door fast-on",
    "parent": 0,
    "comment": "Turn on entry light and notify when front door fast-on during night hours",
    "if": [
      { "(": 1 },
        { "==": { "device": "2A 66 1C 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "2A 66 1C 1", "command": "DON", "parameters": [] },
      { "device": "n001_udmobile", "command": "GV10", "parameters": [ { "name": "Group", "value": 0, "uom": "25" }, { "name": "Sound", "value": 2, "uom": "25" } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) on the front door combined with a night schedule to only act when the door is physically opened at night; then turn the light on and send a mobile SMS notification using UD Mobile's GV10 with Group default and SMS Notification sound."
  }
}
__END_NUCORE_ROUTINE__
